sap.ui.define([
	"com/zmm/commprintout/controller/BaseController",
	"sap/ui/core/mvc/Controller",
	"sap/m/PDFViewer"
], function (BaseController, Controller, PDFViewer) {
	"use strict";
	return BaseController.extend("com.zmm.commprintout.controller.View1", {
		onInit: function () {
			this.getRouter().getRoute("View1").attachPatternMatched(this.onRouteMatched.bind(this));
	//		
		},


		onRouteMatched: function () {
		
		},
		/**
		 *@memberOf com.zmm.commprintout.controller.View1
		 */
		onContinue: function (oEvent) {
			//This code was generated by the layout editor.
			var that = this;
			var sSelectedCat = this.getModel("mainModel").getProperty("/selectedIndex");
			if(sSelectedCat === undefined) sSelectedCat = 0 ;
			sSelectedCat ++;
			var sCategory = "PDC";
			localStorage.removeItem('ConsolisPrintoutData');
			this.getRouter().navTo("View2", {
				category: sCategory.concat(sSelectedCat.toString())
			});
		}
	});
});